            }
        }
        .task(id: course) {
            loading = true
            Task {
                if let course {
                    do {
                        courseInfo = try await downloadCourseOfferInfo(course)
                    } catch {
                        courseInfo = nil
                        print(error.localizedDescription)
                    }

                    loading = false
                }
